import './App.css';
import {useEffect, useState} from 'react';

const options=[
  {
    id:1,
    name:'Products',
    url:'https://dummyjson.com/products'
  },
  {
    id:2,
    name:'Todos',
    url:'https://dummyjson.com/todos'
  },
  {
    id:3,
    name:'Posts',
    url:'https://dummyjson.com/posts'
  }
]
function App() {
  const [inputValue, setInputValue] = useState('');

  const [outputValue, setOutputValue] = useState([]);


  const handleInputChange = (event) => {
    const currentId = event.target.value;
    const selectedObject= options.filter((item)=>{
      return item.id === parseInt(currentId)
    });
    setInputValue(selectedObject);

    console.log("---",selectedObject)
  }

  const handleSubmit = () => {
    fetch(inputValue)
    .then(response => response.json())
    .then(data => {
      setOutputValue(data);
      console.log(outputValue)
    })
    .catch(error => console.error('Error:', error));
  }

  return (
    <div>
      <h2>Component A</h2>
      <label htmlFor="options">Choose option</label>

      <select name="options" id="options" onChange={handleInputChange} >
        {options.map((option,idx)=>(
          <option value={option.id} key={idx}>{option.name}</option>
        ))}
      </select>

      <input type="text" value={inputValue} />
      <button onClick={handleSubmit}>Submit</button>
      <table border="1">
        <thead>
          <tr>
            <th>id</th>
            <th>todo</th>
            <th>completed</th>
            <th>userId</th>
          </tr>
          </thead>
          <tbody>
            {  
            outputValue.map((item)=>(
            <tr>
                <td>{item.id}</td>
                <td>{item.todo}</td>
                <td>{item.completed}</td>
               <td>{item.userId}</td>
             </tr> 
          ))}    
        </tbody>
      </table>
    </div>
  );
}
export default App;
