import './App.css';
import {useEffect, useState} from 'react';

const options=[
  {
    id:1,
    name:'Products',
    url:'https://dummyjson.com/products'
  },
  {
    id:2,
    name:'Todos',
    url:'https://dummyjson.com/todos'
  },
  {
    id:3,
    name:'Posts',
    url:'https://dummyjson.com/posts'
  }
]
function App() {
  const [inputValue, setInputValue] = useState({});

  const [outputValue, setOutputValue] = useState([]);
  const [headers, setHeaders] = useState([]);

  useEffect(()=>{
    if(outputValue[0]){
      const headers = Object.keys(outputValue[0]);
      setHeaders(headers)
    }

  },[outputValue])

  const handleInputChange = (event) => {
    const currentId = event.target.value;
    const selectedObject = options.find((item)=>{
      return item.id === parseInt(currentId)
    });
    setInputValue(selectedObject);
    console.log("-change--",selectedObject)
  }

  const rowTodos=()=>{
    console.log("hiiiiii")
    return(
      outputValue.map((item)=>(
        <tr>
          <td>{item.id}</td>
          <td>{item.todo}</td>
          <td>{item.completed}</td>
          <td>{item.userId}</td>
         </tr> 
      ))
    )
  }
  const handleSubmit = () => {
    fetch(inputValue.url)
    .then(response => response.json())
    .then(data => {
      if(inputValue.id===1){
        setOutputValue(data.products);
      }
      if(inputValue.id===2){
        setOutputValue(data.todos);
      }
      if(inputValue.id===3){
        setOutputValue(data.posts);
      }

    })
    .catch(error => console.error('Error:', error));
  }

  return (
    <div>
      <h2>Component A</h2>
      <label htmlFor="options">Choose option</label>

      <select name="options" id="options" onChange={handleInputChange} >
        {options.map((option,idx)=>(
          <option value={option.id} key={idx}>{option.name}</option>
        ))}
      </select>
      <button onClick={handleSubmit}>Submit</button>
      <table border="1">
        <thead>
        <tr>
          {headers && headers.map((item, idx)=>(
              <th key={idx}>{item}</th>
            ))
          }
        </tr>
          </thead>
          <tbody>
            {inputValue.id===2 && rowTodos() }   
        </tbody>
      </table>
    </div>
  );
}
export default App;
